// services/api.js
import axios from "axios";

const api = axios.create({
  baseURL: "http://casedeep.com:8080/swagger-ui/index.html",
  timeout: 10000,
});

// Interceptors for request and response logging, error handling
api.interceptors.request.use(
  (config) => {
    // Add authentication headers or modify request config
    return config;
  },
  (error) => Promise.reject(error)
);

api.interceptors.response.use(
  (response) => response,
  (error) => {
    // Centralized error handling
    if (error.response.status === 401) {
      // Handle unauthorized errors, maybe redirect to login
    }
    return Promise.reject(error);
  }
);

export default api;
